import React from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js'
import { Bar } from 'react-chartjs-2'
import { Measurements } from './Dashboard';


ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
);


type BarProps = {
    dates: string[],
    emissions: Measurements[]
}


class BarChart extends React.Component<BarProps> {
    constructor(props: BarProps) {
        super(props);
    }

    render() {
        const data = {
            labels: this.props.dates,
            datasets: this.props.emissions.map((measure) => ({
                label: measure.label,
                backgroundColor: measure.color,
                borderColor: measure.color,
                borderWidth: 0,
                data: measure.dataPoints,
                barThickness: 40,
            }
            ))
        }
        return <Bar data={data} options={{ scales: { x: { stacked: true, }, y: { stacked: true, } }, plugins: { tooltip: { callbacks: { label: function (context) { let label = " " + context.formattedValue + " kg CO2-eq"; return label; } } } } }
        } />;
    }
}

export default BarChart;
