import React from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js'
import { Bar } from 'react-chartjs-2'

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
);


type BarProps = {
    data: any
}


class BarChart extends React.Component<BarProps> {
    constructor(props: BarProps) {
        super(props);
    }

    render() {
        return <Bar data={this.props.data} options={{ scales: { x: { stacked: true, }, y: { stacked: true, } }, plugins: { tooltip: { callbacks: { label: function (context) { let label = " " + context.formattedValue + " kg CO2-eq"; return label; } } } } }} />;
    }
}

export default BarChart;
